x = np.arange(1, len(y_AIT504)+1)
if __name__ == '__main__':
    # 进行离散傅里叶变换
    fft_result = np.fft.fft(y_AIT504)
    amplitude = np.abs(fft_result) #幅度
    phase = np.angle(fft_result) #相位
    #添加噪声
    noise = np.random.normal(loc=np.mean(amplitude), scale=np.sqrt(np.var(amplitude)), size=len(fft_result))
    amplitude[:5] = noise[:5]
    #phase[:100] = noise_phase[:100]
    #构建复数数组
    complex_array = amplitude * np.exp(1j * phase)
    # 进行傅里叶逆变换
    ifft_result = np.fft.ifft(complex_array)
    # 绘制原始信号、逆变换结果和频谱图
    plt.figure(figsize=(12, 12))

    plt.subplot(3, 1, 1)
    plt.plot(x, y_AIT504)
    plt.title('Original Signal')
    plt.xlabel('Time')
    plt.ylabel('Amplitude')

    plt.subplot(3, 1, 2)
    plt.plot(np.fft.fftfreq(len(x), d=x[1] - x[0]), amplitude)
    plt.title('Frequency Spectrum')
    plt.xlabel('Frequency (Hz)')
    plt.ylabel('Amplitude')

    plt.subplot(3, 1, 3)
    plt.plot(x, np.real(ifft_result))  # 使用np.real提取实部
    plt.title('Inverse Fourier Transform Result')
    plt.xlabel('Time')
    plt.ylabel('Amplitude')


    plt.tight_layout()
    plt.show()